import React, { Component } from "react";
import "./SliderAlbum.css";
import { Link } from "react-router-dom";
import { getRecords } from "../../services/RecordService";

import image from "../../assets/images/record/abbey_road.jpg"

import MusicPresenter from "../MusicPresenter/MusicPresenter";

class SliderAlbum extends Component {
  state = {};

  constructor(props) {
    super(props);

    this.setupLoop();

    this.state = {

      records: []

    }

  }

  setupLoop() {

    const recordsArray = getRecords();
    recordsArray.then((data)=>{

      this.setState({records: data})

    })

  }

  render() {

    return (
      <div className="container" style={{marginBottom: "3rem"}}>
        <div className="row record-row">
        {
            this.state.records.map(album => (
              <Link to={{pathname:"/record_songs/" + album._id}}>
                <MusicPresenter
                  key={album._id} 
                  name={album.title}
                  band={album.band.name}
                  src={album.pathImage}
                ></MusicPresenter>
              </Link>
            ))}
        </div>
      </div>
    );
  }
}

export default SliderAlbum;
