import React, { Component } from "react";
import "./SliderAlbum.css";
import { Link } from "react-router-dom";
import { getRecords } from "../../services/RecordService";

import image from "../../assets/images/record/abbey_road.jpg"

import MusicPresenter from "../MusicPresenter/MusicPresenter";

class SliderAlbum extends Component {
  state = {};

  constructor(props) {
    super(props);

    this.setupLoop();

    this.state = {

      records: []

    }

  }

  setupLoop() {

    const recordsArray = getRecords();
    recordsArray.then((data)=>{

      this.setState({records: data})

    })

  }

  render() {

    return (
      <div className="container">
        {
            this.state.records.map(album => (
              <Link to={{pathname:"/record_songs/" + album._id}}>
                <MusicPresenter
                  key={album._id} 
                  name={album.title}
                  band={album.band.name}
                  src={album.pathImage}
                ></MusicPresenter>
              </Link>
            ))}
            <div className="card" style={{width: "18rem"}}>
              <div className="card-body">
                <h5 className="card-title">Card title</h5>
                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                <a href="#" className="btn btn-primary">Go somewhere</a>
              </div>
            </div>
            <div id="carouselExampleSlidesOnly" className="carousel slide" data-ride="carousel">
              <div className="carousel-inner">
                <div className="carousel-item active">
                  <img className="d-block w-100" src={image} alt="First slide"/>
                </div>
                <div className="carousel-item">
                  <img className="d-block w-100" src={image} alt="Second slide"/>
                </div>
                <div className="carousel-item">
                  <img className="d-block w-100" src={image} alt="Third slide"/>
                </div>
              </div>
            </div>
      </div>
    );
  }
}

export default SliderAlbum;
